import { useState } from "react"

const Add = () => {

    const [data, setData] = useState()
    const getdata = (e) => {
        setData(e.target.value)
    }

    const additem = (e) => {
        const text=e.target.innerHTML
        const listdata = JSON.parse(localStorage.getItem("todolist"))
        const ndata = {
            title: data,
            ispin: false,
            status: false
        }
        if(text==="Add")
        {
        listdata.push(ndata)
        }
        if(text==="Update")
        {
            const index=e.target.value
            listdata[index]=ndata
            e.target.innerHTML="Add"
        }
        localStorage.setItem("todolist", JSON.stringify(listdata))

    }
    return (
        <>
            <div className="card" style={{ "width": "30rem", textAlign: "center" }}>
                <div className="card-body">
                    <h5 className="title">ToDo List</h5>
                    <input className="form-control" type="text" onChange={getdata} id="txtitem" placeholder="Enter Data" aria-label="default input example"></input>
                    <button className='btn btn-info' onClick={(e)=>additem(e)} id="btnadd">Add</button>
                </div>
            </div>

        </>
    )
}

export default Add
