import { useState, useRef, useEffect } from "react"
import Display from "./Display"
import Pinlist from "./Pinlist"
const Add = () => {

    const [data, setData] = useState()
    // const[tododata, setTododata] = useState()
    const [show, setShow] = useState(1)
    // const list = localStorage.getItem("todolist")
    // const listdata = JSON.parse(list)

    // useEffect(() => {
    //     setTododata(listdata)
    //     console.log("pin"+tododata)
    // }, [])
    // useEffect(() => {
    //     console.log("pind.ad.."+tododata)
    // }, [tododata])
    const [tododata, setTododata] = useState()
    const [todo,setTodo]=useState()
    const list = localStorage.getItem("todolist")
    const listdata = JSON.parse(list)

    useEffect(() => {
        setTododata(listdata)
        console.log(tododata)
    }, [])
    useEffect(() => {
        const list = localStorage.getItem("todolist")
        const listdata = JSON.parse(list)
        setTodo(listdata)
    }, [tododata])

    const getdata = (e) => {
        setData(e.target.value)
    }
    const item = useRef()
    const additem = (e) => {
       // setShow(show + 1)
        const text = e.target.innerHTML
        // const listdata = JSON.parse(localStorage.getItem("todolist"))
        if (text === "Add") {
            const ndata = {
                title: data,
                ispin: false,
                status: false
            }
            todo.splice(0,0,ndata)
                setTododata(listdata)
            document.getElementById("msg").innerHTML = "Successfully Added";
        }
        if (text === "Update") {
            const index = e.target.value
            const ndata = {
                title: data,
                ispin: listdata[index].ispin,
                status: listdata[index].status
            }
            todo[index] = ndata
             setTododata(listdata)
            e.target.innerHTML = "Add"
            item.current.value = ""
            document.getElementById("msg").innerHTML = "Successfully Updated";
        }
        localStorage.setItem("todolist", JSON.stringify(todo))
        document.getElementById("liveToast").style.display = "block"
    }
    const close = () => {
        document.getElementById("liveToast").style.display = "none"
    }
    const donetask = (e, index) => {
        if (e.target.checked) {
            todo[index].status = true
            setTododata(todo)
            document.getElementById("msg").innerHTML = "Task Completed"
            document.getElementById("liveToast").style.display = "block"
        }
        else {
            todo[index].status = false
            setTododata(todo)
            document.getElementById("msg").innerHTML = "Task not Completed"
            document.getElementById("liveToast").style.display = "block"
        }
        localStorage.setItem("todolist", JSON.stringify(todo))
    }
    return (
        <>
            <div className="card" style={{ "width": "30rem", textAlign: "center" }}>
                <div className="card-body">
                    <h5 className="title">ToDo List</h5>
                    <input className="form-control" type="text" onChange={getdata} id="txtitem" ref={item} placeholder="Enter Data" aria-label="default input example"></input>
                    <button className='btn btn-info' onClick={(e) => additem(e)} id="btnadd">Add</button>
                </div>
            </div>

            <Pinlist val={show} donetask={donetask}/>

            <Display val={show} donetask={donetask}/>

            <div className="toast-container position-fixed top-0 end-0 p-3">
                <div id="liveToast" className="toast align-items-center text-bg-success border-0" role="alert" aria-live="assertive" aria-atomic="true">
                    <div className="d-flex">
                        <div className="toast-body" id="msg">

                        </div>
                        <button type="button" className="btn-close btn-close-white me-2 m-auto" data-bs-dismiss="toast" aria-label="Close" onClick={close}></button>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Add
