import { useEffect, useState } from "react"

const Todo = () => {

    const [data,setData]=useState()
    const [pin,setPin]=useState(false)
    const [listdata,setListdata]=useState([])
    useEffect(()=>{
        listdata.push(localStorage.getItem("todolist"))
        console.log(listdata)
    },[])
    const getdata=(e)=>
    {
        setData(e.target.value)
    }

    const additem=()=>
    {
      //  const nomineedata = JSON.parse(localStorage.getItem("todolist"))
            const ndata = {
                title : data,
                ispin : pin
            }
            listdata.push(ndata)
            localStorage.setItem("todolist", JSON.stringify(listdata))
        console.log("added")

    }
    return (
        <>
        <h1>Hello</h1>
            <div className="card" style={{"width":"18rem"}}>
                <div className="card-body">
                    <h5 className="card-title">ToDo List</h5>
                    <input className="form-control" type="text" onChange={getdata} placeholder="Enter Data" aria-label="default input example"></input>
                    <button className='btn btn-info' onClick={additem}>Add</button>
                </div>
            </div>

            </>
    )
}

export default Todo
