import { useState } from "react";

const Display = () => {

    const listdata=JSON.parse(localStorage.getItem("todolist"))
    const data=[];
    listdata.forEach((item,index) => {
        data.push(<><li className="list-group-item" key={index}>{item.title}
            <button onClick={()=>deletedata(index)} style={{float:"right"}} >&#10060;</button>
            <button onClick={()=>updatedata(index)} style={{float:"right"}} >&#9998;</button></li></>)
    });

    const updatedata=(index)=>{
        console.log("up"+index)
    }

    const deletedata=(index)=>{
        const listdata=JSON.parse(localStorage.getItem("todolist"))
        listdata.splice(index,1)
        localStorage.setItem("todolist",JSON.stringify(listdata))
    }

    return (
        <>

            <div className="card" style={{"width":"30rem"}}>
                <ul className="list-group list-group-flush">
                    {data}
                </ul>
            </div>
        </>
    )
}

export default Display
