  const[tododata, setTododata] = useState()
    const list = localStorage.getItem("todolist")
    const listdata = JSON.parse(list)

    useEffect(() => {
        setTododata(listdata)
        console.log("pin"+tododata)
    }, [])
    useEffect(() => {
        console.log("pind..."+tododata)

    }, [tododata])
  return (
    <div className="App">
       <Add />
      <Pinlist />
      <Display /> 
      {/* <Demo/> */}
    </div>
  );
}

export default App;
