import { useState } from "react"

const Add = () => {

    const [data, setData] = useState()
    localStorage.setItem("pin","1")
    const getdata = (e) => {
        setData(e.target.value)
    }

    const additem = () => {
        const listdata = JSON.parse(localStorage.getItem("todolist"))
        const ndata = {
            title: data,
            ispin: false,
            status: false
        }
        listdata.push(ndata)
        localStorage.setItem("todolist", JSON.stringify(listdata))
        console.log("added")
    }
    return (
        <>
            <div className="card" style={{ "width": "30rem", textAlign: "center" }}>
                <div className="card-body">
                    <h5 className="title">ToDo List</h5>
                    <input className="form-control" type="text" onChange={getdata} placeholder="Enter Data" aria-label="default input example"></input>
                    <button className='btn btn-info' onClick={additem}>Add</button>
                </div>
            </div>

        </>
    )
}

export default Add
