//&#x2705;

import { useRef } from "react";


const Display = () => {

    const done=useRef()
    const listdata = JSON.parse(localStorage.getItem("todolist"))
    const data = [];
    listdata.forEach((item, index) => {
        data.push(<>{item.ispin || <li className="list-group-item" key={index}><div style={{ float: "left" }}>{item.title}</div>
            <div style={{ float: "right" }}>
                <button onClick={() => pindata(index)}  >&#x1F4CC;</button>&nbsp;
                <input type="checkbox" onChange={()=>{donetask(index)}}/><span ref={done}>Done</span>
                <br /><br />
                <button onClick={() => deletedata(index)}  >&#10060;</button>&nbsp;
                <button onClick={() => updatedata(index)}  >&#9998;</button></div></li>}</>)
    });


    const donetask=()=>{
        const isdone=done.current.value
        console.log(isdone)
    }

    const pindata = (index) => {

        listdata[index].ispin = true
        localStorage.setItem("todolist", JSON.stringify(listdata))
    }

    const updatedata = (index) => {
        console.log("up" + index)
    }

    const deletedata = (index) => {
        listdata.splice(index, 1)
        localStorage.setItem("todolist", JSON.stringify(listdata))
    }

    return (
        <>

            <div className="card" style={{ "width": "30rem" }}>
                <ul className="list-group list-group-flush">
                    {data}
                </ul>
            </div>
        </>
    )
}

export default Display
